CREATE TABLE `article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `category` int(10) unsigned NOT NULL,
  `modified` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `visible` tinyint(1) DEFAULT NULL,
  `owner` int(10) unsigned NOT NULL,
  PRIMARY KEY (`id`),
  KEY `fk_article_category1_idx` (`category`),
  KEY `fk_article_user1_idx` (`owner`),
  CONSTRAINT `fk_article_category1` FOREIGN KEY (`category`) REFERENCES `category` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_article_user1` FOREIGN KEY (`owner`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;
INSERT INTO `article` VALUES (3,6,'2014-01-13 11:11:45',1,1);

CREATE TABLE `article_has_tag` (
  `article_id` int(11) NOT NULL,
  `tag_id` int(11) NOT NULL,
  PRIMARY KEY (`article_id`,`tag_id`),
  KEY `fk_article_has_tag_tag1_idx` (`tag_id`),
  KEY `fk_article_has_tag_article1_idx` (`article_id`),
  CONSTRAINT `fk_article_has_tag_article1` FOREIGN KEY (`article_id`) REFERENCES `article` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_article_has_tag_tag1` FOREIGN KEY (`tag_id`) REFERENCES `tag` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;



CREATE TABLE `articleloc` (
  `article` int(11) NOT NULL,
  `locale` int(11) NOT NULL,
  `content` mediumtext CHARACTER SET utf8,
  `description` varchar(5000) CHARACTER SET utf8 DEFAULT NULL,
  `title` varchar(1000) CHARACTER SET utf8 DEFAULT NULL,
  PRIMARY KEY (`article`,`locale`),
  KEY `fk_articleloc_locale1_idx` (`locale`),
  CONSTRAINT `fk_articleloc_article1` FOREIGN KEY (`article`) REFERENCES `article` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_articleloc_locale1` FOREIGN KEY (`locale`) REFERENCES `locale` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;
INSERT INTO `articleloc` VALUES (3,1,'<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>ДРЕВНОСТИ ГРОДНО // </strong>Белорусская цифровая библиотека&nbsp; - library.by (c).- Опубл.: 07.12.2010.- Режим доступа: library.by/portalus/modules/belarus/readme.php.- 17.12.2013</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp;На пограничье Руси и Польши в землях, некогда заселенных воинственным племенем ятвягов, в XII в. возник город Гродно. Русские летописи двенадцать раз вскользь упоминают о нем. Но их сведения настолько отрывочны и разрозненны, что не только не дают ясного представления о его ранней истории, но и явились поводом для спора о местонахождении города. Многим казалось немыслимым отождествлять летописное Городно, связанное с волынской княжеской линией, с неманским Гродно. Делались попытки связать Гродно русских летописей с Давид-Городком, расположенным в низовьях Горыни. В качестве доказательства указывали на то, что отца первого известного гродненского князя звали Давидом Игоревичем. Но проведенные польскими археологами раскопки Давид-Городка не подтвердили одного летописного свидетельства. В записи 1183 г. сказано следующее: &laquo;Того же лета Городен погоре всь и церкы каменая от блистания молние и шибения грома&raquo;. В Давид-Городке никаких каменных церквей не оказалось. Он был застроен деревом. Между тем в неманском Гродно до сих пор сохраняются руины каменного храма Бориса и Глеба XII в. Чтобы разрешить этот спор, нужно было провести раскопки и в Гродно. Исследование начали польские археологи в 1932 - 1939 гг. и закончили советские в 1949 г. [6].</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">В пределах современного Гродно имеется несколько мест, с которыми может быть связана его древнейшая история. На высоком правом берегу Немана речкой Городничанкой образован треугольный мыс, известный и теперь под названием Старого замка или Замковой горы. Через овраг от него расположено высокое плато Нового замка. На противоположном берегу Городничанки сохранились остатки Борисоглебской церкви XII в.</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Выходы древнего культурного слоя зафиксированы в радиусе до 500...600 м от Замковой горы. Приблизительно в 650 м к юго-востоку от нее, судя по старым описям города, имелась Курганная улица, которая, по-видимому, указывает на существование здесь курганного могильника, уничтоженного позже городской застройкой. </span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Раскопки велись на территории Старого замка, который явился ядром древнего Гродно. Культурный слой достигал здесь местами десятиметровой толщины. Нижние пласты толщиной от 1,5 до 5 м содержали остатки древнерусского Гродно Х1...ХШ вв. Особенности почвы благоприятствовали хорошей сохранности органических веществ.</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Как показали раскопки, город ведет свое происхождение от крепости, возникшей на Замковой горе во второй половине или конце XI в. Первоначально она имела военное значение. В составе населения заметная роль принадлежала вооруженному люду, конным дружинникам. В раскопках найдены пять различного типа шпор, обломок клинка кинжала или копья, железные втульчатые наконечники сулиц, железные и костяные четырехгранные стрелы. О состоятельности жителей крепости свидетельствует обилие замков и ключей от дверей, сундуков и ларцов. Крепость была застроена деревянными срубами. Один из них имел подполье и, возможно, слюдяные окна. Сохранились остатки деревянных мостовых. Совершенно неожиданным оказался результат изучения костных остатков. Половина костей принадлежала диким животным, прежде всего оленю, зубру, кабану. Охотничья добыча была важной статьей хозяйства. Возможно, дичь представляла разновидность феодальных повинностей с крестьян, однако следует учитывать и то обстоятельство, что охота на крупных животных всегда была излюбленным &laquo;спортом&raquo; привилегированных слоев, к которым, несомненно, относились дружинники крепости. Лесные пущи Понеманья изобиловали тогда дичью.</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Ремесла представлены еще слабо. Помимо продукции гончаров, найдены предметы, связанные с ювелирным литьем: обломки тигельков и литейные формочки. В одной из них отливались щитки для перстней-печатей. Можно говорить о существовании в крепости косторезного и деревообрабатывающего ремесла. Почти все вещи имеют типичный общерусский облик. Крепость была тесно связана с Русью. В культурных напластованиях древнейшего периода найдены стеклянные браслеты, шиферные пряслица, бусы, киевские писанки, амфоры, обломки стеклянной посуды. Вместе с тем в керамике можно уловить и местные особенности, выражающиеся в украшении посуды своеобразным штампованным орнаментом или налепными орнаментированными валиками.</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">В начале XII в. крепость сгорела. Ее дальнейшая история представляет собой процесс постепенного превращения в княжескую резиденцию и столицу княжества. Планировочные работы сравняли с лицом земли остатки пожарища, сгоревшие строения засыпаются привозным грунтом, выравнивается площадка. В центре крепости сооружается кирпичный храм, а несколько позже &mdash; кирпичные башни на берегу Немана. В восточной части крепости, вероятно, отстраивается княжеский двор. Позже там был дворец Витовта, а затем Стефана Батория. Застраивается деревянными домами территория вокруг церкви. Со временем застройка становится все более плотной. Ориентировка церкви определяет ориентировку деревянных построек. С севера на юг мимо западной стены храма через крепость прошла улица, мощенная деревом. Другая соединила западную окраину крепости с городскими воротами на восточном краю.</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Культурные отложения ХП...середины XIII в. составили толщу от 1,6 до 2 м. За это время сооружения на вскрытом участке перестраивались пять раз. Облик застройки и характер строительных приемов типичен для русских городов лесной полосы. Жилые постройки имели размеры от 3,40х3,40 м до 4х4 м. Рубились они преимущественно из сосны, углы связывались в обло(?). Под некоторыми постройками прослежены &laquo;стулья&raquo; из вертикальных бревен или камней. Одна изба имела завалину. Внутри настилались дощатые полы. В углу помещалась глинобитная с включением камня печь на деревянной платформе. Хозяйственные постройки отличались лишь меньшими размерами. Внутренние дворики мостились деревом. Некоторые, возможно, имели навесы на столбах. </span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Хотя ни княжеский дворцовый комплекс, ни постройки дружинников не попали на исследованную площадь, довольно многочисленные и разнообразные предметы вооружения по-прежнему указывают на аристократический характер района и, несомненно, большую роль дружинного элемента в составе населения крепости. В слоях XII... середины XIII в. найдена целая серия шпор, наконечники стрел и копий, бронзовая булава. Одна шпора была богато украшена бронзовой инкрустацией, напоминающей когти хищной птицы с роговыми чешуйками. Стрелы различные по типу: ромбовидные, треугольные с шипами, граненые, специально предназначенные для пробивания кольчуги. Бронзовая булава с четырьмя выступающими пирамидальными шипами очень маленького размера. Насаживалась она на тонкую рукоять. Для борьбы с неприятельской кавалерией применялся так называемый &laquo;чеснок&raquo; с зазубренными остриями.</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">В материалах, относящихся к этому этапу жизни города, значительно полнее представлена хозяйственная жизнь населения. В керамическом производстве появилась новая статья. В связи с началом княжеского и храмового строительства в Гродно появляются кирпичные сараи и печи для обжига, где изготовлялись плоский и фасонный кирпич и специальные поливные плитки. Плитки эти различны по размерам и форме, покрыты желтой, зеленой и коричневой глазурью. Из них можно было составлять красивые замысловатые узоры.&nbsp;</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Шахматная фигура &mdash; ладья. </span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Хорошо представлено сапожное ремесло в Гродно. Часть сапожников жила и работала в самой крепости. Обувь обычная для Руси: поршни и мягкие туфли. Особо следует отметить находку кожаного крепления лыж. </span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Очень много изделий из дерева: посуда, черпаки, клепки от ведер и бочек, гребни, лопаты, весла, разнообразные колотушки, детали ткацкого станка и т. д. Некоторые отрасли деревообработки, видимо, выделились в особые ремесла. Найдены детские игрушки из дерева: лодочка из сосновой коры, стрелы и мечи. Интересна находка деревянного колка струнного музыкального инструмента. Форма его такая же, как и современных. </span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">О развитии ювелирного дела свидетельствуют найденные льячки-тигельки, одиннадцать литейных форм, в которых отливались лунницы, круглая подвеска с изображением креста, крестообразная подвеска, звездчатая семилучевая ложнозерненая подвеска, ложнозерненая буса, трехбусинные ложнозерненые серьги, пластинчатый перстень. Некоторые формы составлялись из нескольких створок. На одной форме процарапан &laquo;знак Рюриковичей&raquo;. Интересно, что в раскопках не встречено ни одной вещи, отлитой в найденных формах. Значит, продукция гродненских ювелиров быстро находила своего потребителя. </span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">В крепости найдены отходы косторезного производства и полуфабрикаты. Достаточно много готовых изделий из кости: рукоятки ножей, гребни, застежки и т. д. Многие изделия нарядно орнаментированы, сделаны на токарном станке.</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Уникальной по своей художественной ценности и реалистичности изображения является каменная скульптурка ладьи на подставке. Ее размеры 55х45 мм. Оба ее носа острые, что характерно для боевой ладьи с двусторонним ходом. По бокам &mdash; круглые отверстия для весел и по три с каждой стороны миндалевидных щита, типичных для XII...XIII вв. На палубе помещены четыре человеческие фигурки: одна на корме, одна на носу, две посередине. Скульптура является поразительно точной иллюстрацией следующего летописного текста 1151 г.: &laquo;бе бо исхитрил лодье дивно: беша бо в них гребьци гребуть невидимо, токмо весла видети, а человек бяше не видети, бяхуть бо лодье покрыты досками, бяхуть бо борци стояще горе и стреляюще, а кормника два беста, один на корме, а другый на носе... &raquo;</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Ладья, несомненно, исполненная русским мастером по образу и подобию реальных суден XII в., представляет собой одну из фигур высокохудожественного набора шахмат, в котором &laquo;ладья&raquo; заменяет &laquo;колесницу&raquo;, характерную для шахматных наборов других стран. </span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Из импортных вещей следует отметить хранившийся в Коложской церкви бронзовый водолей, представляющий собой фигуру рыцаря верхом на коне. Водолей отлит в мастерской Гильдесгейма (Нижняя Саксония) в XIII в. </span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Картина жизни Гродненской крепости будет неполной, если не отметить находки, связанные с земледелием, скотоводством, охотой и рыболовством. Население Гродненской крепости, как и в других городах, еще не порвало с сельским хозяйством. Об этом говорят находки железного сошника от трех или четырехзубой сохи, обломка косы-горбуши, железных серпов. Злаки представлены рожью, пшеницей, ячменем, овсом, просом, гречихой. </span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Интересно отметить, что удельный вес охотничьей добычи в XII...XIII вв. не только не уменьшился по сравнению с начальным периодом, но и несколько увеличился: кости домашних животных составили 32,4%, диких &mdash; 67,6%. О занятиях рыболовством свидетельствуют находки остроги, рыболовных крючков, грузил.&nbsp;</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">XIII... XIV вв. были неспокойными для Гродно. Сначала борьба между литовскими и русскими князьями за обладание Черной Русью, а затем борьба с Тевтонским орденом не способствовали развитию города. Гродно и его окрестности не раз подвергались опустошению. Вещевой материал, происходящий из слоев XIII...XIV вв., исключительно бедный и однообразный, воссоздает картину некоторого упадка культуры [6]. </span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Особого рассмотрения требует гродненская монументальная архитектура, по праву считающаяся ярким проявлением культуры Гродно XII в. Самым ранним архитектурным памятником является так называемая Нижняя церковь, открытая раскопками в центре крепости. Уже она отмечена печатью архитектурного своеобразия, получившего развитие и воплощение в последующих каменных постройках Гродно и некоторых других городов.</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Нижняя церковь, рухнувшая во время пожара 1183 г., сохранила свои стены на высоту до 3,5 м. Сложены они из тонкого плитчатого кирпича размером 26 х 15 Х 2,5...3,5 см обычной для XII в. техникой порядовой кладки на толстых швах связующего раствора. Фундамент сделан из камней, пересыпанных землей. Необычен план и общая композиция храма. </span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Храм имеет всего одну плоскую абсиду. Две другие скрыты в толще угловых стен. Столбов шесть. Причем четыре западных столба мощнее восточной пары, что, указывает на то, что купол храма был смещен к западу. Это придавало церкви необычную асимметричную композицию. Столбы имели восьмигранную форму. Для улучшения акустических условий в кладку стен были уложены многочисленные глиняные кувшины-голосники, выделявшиеся своими темными отверстиями на розовато-белом фоне кирпича и извести. Пол храма был богато выстлан цветными майоликовыми плитками желтого, зеленого и коричневого тонов. Под куполом здания плитки различной формы и окраски составили сложный нарядный узор. Но еще более необычно было наружное оформление храма. В нижнюю часть стен были вставлены огромные цветные валуны с шлифованной поверхностью, а выше &mdash; на плоских лопатках, членивших наружный фасад здания, и на срезанных углах были размещены различные геометрические фигуры из майоликовых плиток. Такое красочное убранство Нижней церкви выделяет ее среди строгих храмовых построек юга Руси.</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">После гибели Нижней церкви была построена Борисоглебская церковь на Коложе, которой, по-видимому, предшествовало воздвижение каменных башен, открытых в западной и южной части Верхнего замка, Башни сложены из кирпича несколько более крупного, чем кирпич Нижней церкви, на известковом растворе без примеси кирпичной крошки. Южная башня, по-видимому, была квадратная в плане 10х10 м, первоначальные размеры другой установить невозможно.</span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Фасады южной башни, как и Нижней церкви, украшены вставками из необработанного камня светло-розового, темно-коричневого и серого цвета. Возможно, что верхняя часть сооружения имела майоликовый полихромный убор. Назначение башен, очевидно, военное. Можно предполагать, что они соединялись между собой валами [6].</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Борисоглебская церковь еще в большей степени выразила новые художественные принципы гродненских зодчих. Интерьер этого шестистолпного трехабсидного храма поражает своим исключительным своеобразием и эффектностью. Стройные круглые столбы, легкие балконы вдоль боковых стен, обилие света, проникавшего через многочисленные окна стен и барабана, делали его похожим на просторный роскошный зал. Стены были обильно заполнены голосниками. Более богатым и продуманным был и внешний облик церкви. Убранство фасадов камнем и майоликой стало особенно красочным. Здесь для украшения использовались сравнительно небольшие и мелкие камни. Были учтены инженерные просчеты, допущенные при строительстве Нижней церкви, крупные валунные камни которой сильно ослабили прочность стен. Каменные вставки с большим художественным вкусом сочетались с фигурами крестов из цветных майоликовых плиток и отдельными квадратами плиток. Подсчитано, что на внутреннее и внешнее оформление церкви использовано не менее 16 различных форм майолики. </span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Роскошная многокрасочность убранства гродненских памятников позволяет говорить о сложившейся здесь особой архитектурной школе XII в. Очевидно, удаленность Гродно от главных центров религиозной жизни с ее канонизацией традиционных архитектурных форм и отсутствие контроля над церковным строительством создали здесь благоприятные условия для воплощения в монументальных постройках народных эстетических представлений. </span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Гродненские постройки были хорошо известны зодчим других русских областей. В некоторых постройках XII в. явно ощущается подражание гродненским приемам убранства фасадов.</span></span></p>\n<p style=\"text-align: justify;\">\n	<span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Список использованной литературы: </span></span><span style=\"font-size:12px;\"><span style=\"font-family:times new roman,times,serif;\">Э.М. Загорульский &quot;Архитектура Беларуси&quot; - БГУ, 2001 г.</span></span></p>\n','Краткий исторический очерк с характеристикой памятников истории и архитектуры','Древности Гродно');

